function kpi_ho_NCE = bmmo_parse_kpi_ho_NCE_lis(stats_rep)
%
% function kpi_ho_NCE = bmmo_parse_kpi_ho_NCE_lis(stats_rep)
%
%Given the Controlled input KPIs, Outlier coverage and statistics from the LIS job report, parse the
%contents into a BMMO-NXE kpi structure
% Input:  
%   stats_rep:  Controlled input overlay metrics (Max, 3s) 
% 
% Output:
%   kpi_input :  KPI (99.7, M3s, Max, 3s) Intrafield 18par/33par NCE as generated by
%   BMMO model
%
% Changelog
% 20201030 LUDU Creation
% 20201119 OGIE 33par version, copied from ho_ version

fields_rep = {'TotalFiltered', 'TotalUnfiltered'};
fields     = {'total_filtered', 'total_unfiltered'};

    for i = 1:length(fields)
        kpi_ho_NCE.(fields{i}) = bmmo_parse_kpi_overlay_lis(stats_rep(1).(fields_rep{i}), stats_rep(2).(fields_rep{i}), 'kpi');
        pi_ho_NCE.(fields{i}) = bmmo_parse_kpi_overlay_lis(stats_rep(1).(fields_rep{i}), stats_rep(2).(fields_rep{i}), 'Max');
        kpi_ho_NCE.(fields{i}) = bmmo_add_missing_fields(kpi_ho_NCE.(fields{i}), pi_ho_NCE.(fields{i}));
    end